declare module "type-checks" {
  export const isUndef: (val: any) => boolean;
  export const isNull: (val: any) => boolean;
  export const isNullOrUndef: (val: any) => boolean;
  export const isString: (val: any) => boolean;
  export const isNumber: (val: any) => boolean;
  export const isNumberOrString: (val: any) => boolean;
  export const isBool: (val: any) => boolean;
  export const isFunction: (val: any) => boolean;
  export const isDate: (val: any) => boolean;
  export const isFormatDate: (val: any) => boolean;
  export const isFormatDateTime: (val: any) => boolean;
  export const isFormatDateUTC: (val: any) => boolean;
  export const isObject: (value: any) => boolean;
  export const isEmpty: (val: any) => boolean;
}

